mapper.select:
SELECT * FROM tb_user

     AND id = #{id} 
     AND username = #{username} 
     AND password = #{password} 
     AND name = #{name} 
     AND age = #{age} 
     AND sex = #{sex} 
     AND birthday = #{birthday} 
     AND note LIKE CONCAT('%',#{note},'%') 
     AND created = #{created} 
     AND updated = #{updated}

mapper.delete:
DELETE FROM tb_user

     AND id = #{id} 
     AND username = #{username} 
     AND password = #{password} 
     AND name = #{name} 
     AND age = #{age} 
     AND sex = #{sex} 
     AND birthday = #{birthday} 
     AND note LIKE CONCAT('%',#{note},'%') 
     AND created = #{created} 
     AND updated = #{updated}
